import org.junit.Test;import static org.junit.Assert.*;/** * Created by Sonny on 6/3/2017. */public class CustomerTest {    Customer sonny = new Customer("Sonny");    @Test    public void getName() throws Exception {        assertEquals("Sonny", sonny.getName());    }    @Test    public void testUnhappy() throws Exception {        assertFalse(sonny.isHappy());    }    @Test    public void testHappy() throws Exception {        sonny.eatFood();        assertTrue(sonny.isHappy());    }}